@Preview
@Composable
private fun WeekCalendarCardPreview() {
    val today = Clock.System
        .now()
        .toLocalDateTime(TimeZone.currentSystemDefault())
        .date
    WeekCalendarCard(
        selectedDate = today,
        templates = emptyList(),
        onDateSelected = {},
        onCenterDateChanged = {},
    )
}

