#!/usr/bin/env python3
import sys, os, io
from pathlib import Path

# Remove comments from various source files across the repo.
# - Backs up original file to <file>.bak
# - Supports C-style (//, /* */) comment removal for .kt, .java, .kts, .gradle, .groovy, .js, .ts, .c, .cpp, .h, .swift
# - Supports XML/HTML <!-- --> removal for .xml, .html, .md
# - Supports hash '#' line comments for .properties, .pro, .env, .sh, .bash, .yml, .yaml (preserve shebang)
#
# This script uses a conservative lexer for C-style comments that avoids removing // or /* */ inside
# string literals (single, double, and triple-quoted strings). It's not a full parser but works well
# for typical source files in this repository.

SKIP_DIRS = {".git", "build", "out", "gradle", "node_modules"}
MAX_FILE_SIZE = 2 * 1024 * 1024  # 2MB

c_style_exts = {'.kt', '.java', '.kts', '.gradle', '.groovy', '.js', '.ts', '.jsx', '.tsx', '.c', '.cpp', '.h', '.hpp', '.swift', '.m', '.mm', '.scala', '.rs'}
xml_like_exts = {'.xml', '.html', '.xhtml', '.htm', '.md'}
hash_line_exts = {'.properties', '.pro', '.env', '.sh', '.bash', '.yml', '.yaml'}

changed = []
errors = []


def strip_c_style_comments(text):
    out = []
    i = 0
    n = len(text)
    state = 'NORMAL'
    while i < n:
        ch = text[i]
        # NORMAL state
        if state == 'NORMAL':
            if text.startswith('"""', i):
                # triple-quoted string start
                out.append('"""')
                i += 3
                state = 'TRIPLE'
                continue
            if ch == '"':
                out.append(ch)
                i += 1
                state = 'DQUOTE'
                continue
            if ch == "'":
                out.append(ch)
                i += 1
                state = 'SQUOTE'
                continue
            if text.startswith('//', i):
                # skip until end of line
                i += 2
                while i < n and text[i] != '\n':
                    i += 1
                # preserve newline if present
                continue
            if text.startswith('/*', i):
                i += 2
                # skip until */
                while i < n and not text.startswith('*/', i):
                    i += 1
                i += 2 if i < n else 0
                continue
            # otherwise copy char
            out.append(ch)
            i += 1
            continue
        elif state == 'DQUOTE':
            if ch == '\\':
                # escape sequence
                out.append(text[i:i+2])
                i += 2
                continue
            if ch == '"':
                out.append(ch)
                i += 1
                state = 'NORMAL'
                continue
            # inside double-quote
            out.append(ch)
            i += 1
            continue
        elif state == 'SQUOTE':
            if ch == '\\':
                out.append(text[i:i+2])
                i += 2
                continue
            if ch == "'":
                out.append(ch)
                i += 1
                state = 'NORMAL'
                continue
            out.append(ch)
            i += 1
            continue
        elif state == 'TRIPLE':
            # keep everything until next triple quote
            if text.startswith('"""', i):
                out.append('"""')
                i += 3
                state = 'NORMAL'
                continue
            # otherwise append char
            out.append(ch)
            i += 1
            continue
    return ''.join(out)


def strip_xml_comments(text):
    # remove <!-- ... --> occurrences
    out = []
    i = 0
    n = len(text)
    while i < n:
        if text.startswith('<!--', i):
            i += 4
            while i < n and not text.startswith('-->', i):
                i += 1
            i += 3 if i < n else 0
            continue
        out.append(text[i])
        i += 1
    return ''.join(out)


def strip_hash_comments(text, preserve_shebang=True):
    lines = text.splitlines(keepends=True)
    out_lines = []
    for idx, line in enumerate(lines):
        stripped = line.lstrip()
        if preserve_shebang and idx == 0 and stripped.startswith('#!'):
            out_lines.append(line)
            continue
        # find first non-whitespace char
        leading_ws_len = len(line) - len(stripped)
        if stripped.startswith('#'):
            # remove entire line
            # but keep newline if present to preserve line numbers
            if line.endswith('\n'):
                out_lines.append('\n')
            else:
                # drop last line completely
                pass
            continue
        # inline hash comments: remove ' #...' only if hash is not in a value? We will remove inline comments after a space
        # naive approach: if ' #' appears, strip from that point
        idx_hash = line.find(' #')
        if idx_hash != -1:
            # keep up to idx_hash and newline
            prefix = line[:idx_hash]
            if line.endswith('\n'):
                prefix += '\n'
            out_lines.append(prefix)
        else:
            out_lines.append(line)
    return ''.join(out_lines)


def should_process(path: Path):
    # skip large files and files in SKIP_DIRS
    try:
        if path.is_dir():
            return False
    except Exception:
        return False
    parts = set(path.parts)
    if parts & SKIP_DIRS:
        return False
    if path.name.endswith('.bak') or path.name.endswith('.orig'):
        return False
    try:
        if path.stat().st_size > MAX_FILE_SIZE:
            return False
    except Exception:
        return False
    return True


def process_file(path: Path):
    try:
        text = path.read_text(encoding='utf-8')
    except Exception as e:
        errors.append((str(path), f'read_error: {e}'))
        return
    ext = path.suffix.lower()
    new_text = None
    if ext in c_style_exts:
        new_text = strip_c_style_comments(text)
    elif ext in xml_like_exts:
        new_text = strip_xml_comments(text)
    elif ext in hash_line_exts:
        new_text = strip_hash_comments(text, preserve_shebang=True)
    else:
        # skip other types
        return
    if new_text is not None and new_text != text:
        # backup
        bak = path.with_suffix(path.suffix + '.bak')
        try:
            path.rename(bak)
            bak.write_text(text, encoding='utf-8')
            path.write_text(new_text, encoding='utf-8')
            changed.append(str(path))
        except Exception as e:
            errors.append((str(path), f'write_error: {e}'))


if __name__ == '__main__':
    root = Path('.').resolve()
    print('Scanning for files...')
    for p in root.rglob('*'):
        if not should_process(p):
            continue
        if p.suffix.lower() in c_style_exts | xml_like_exts | hash_line_exts:
            process_file(p)
    print('\nSummary:')
    print(f'  Files changed: {len(changed)}')
    for f in changed[:200]:
        print('   -', f)
    if len(changed) > 200:
        print('   - ... (truncated)')
    if errors:
        print('\nErrors:')
        for e in errors:
            print('   -', e)
    print('\nBackups of modified files have been saved with a .bak suffix on the original filename.')
    if not changed:
        print('No changes made.')

